$(document).ready( function() { 

	$('.main-menu-mobile').click(function(){
  	$('.main-menu').toggleClass('show-menu');
  	});



  	// scroll page

  $('a[href*=#]').click(function() {

   if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'')
       && location.hostname == this.hostname) {

           var $target = $(this.hash);

           $target = $target.length && $target || $('[name=' + this.hash.slice(1) +']');

           if ($target.length) {

               var targetOffset = $target.offset().top;

               $('html,body').animate({scrollTop: targetOffset}, 1000);

               return false;

          }

     }
  });

  $('.years').click(function(){
    
    $('.years').toggleClass('active-dropdown');  
  });

  $('.uni-drop').click(function(){
    
    $('.uni-drop').toggleClass('active-dropdown');  
  });


  $('.ask-team').click(function(){
    
    $('.ask-team').toggleClass('active-dropdown');  
  });


  $('.team').click(function(){
    
    $('.team').toggleClass('active-dropdown');  
  });

  $('.machine').click(function(){
    
    $('.machine').toggleClass('active-dropdown');  
  });


}); //--close all functions